using System;\nusing System.Text.RegularExpressions;\n\npublic class MarkdownConverter\n{\n    public static string ConvertMarkdownToHtml(string markdownText)\n    {\n        // Convert **bold** to <strong>bold</strong>\n        string htmlText = Regex.Replace(markdownText, @"\*\*(.+?)\*\*", "<strong>$1</strong>");\n        \n        // Convert *italic* to <em>italic</em>\n        htmlText = Regex.Replace(htmlText, @"\*(.+?)\*", "<em>$1</em>");\n        \n        return htmlText;\n    }\n\n    public static void Main()\n    {\n        string markdownText = "This would be *italicized* text and this would be **bold** text";\n        string htmlText = ConvertMarkdownToHtml(markdownText);\n        \n        Console.WriteLine(htmlText);\n    }\n}