public class ColorNum\n{\n    public string m_Color { get; set; }\n    public int m_Number { get; set; }\n\n    public ColorNum(string color, int number)\n    {\n        m_Color = color;\n        m_Number = number;\n    }\n}\n\nList<ColorNum> ColorNumList = new List<ColorNum>\n{\n    new ColorNum("Red", 25),\n    new ColorNum("Blue", 5),\n    new ColorNum("Red", 11),\n    new ColorNum("White", 25)\n};\n\n// Custom comparison method to sort based on m_Color\nColorNumList.Sort((x, y) =>\n{\n    if (x.m_Color == "Red" && y.m_Color != "Red")\n    {\n        return -1;\n    }\n    else if (x.m_Color != "Red" && y.m_Color == "Red")\n    {\n        return 1;\n    }\n    else\n    {\n        return 0; // No change in order if both are "Red" or neither is "Red"\n    }\n});\n\n// Print sorted list\nforeach (var item in ColorNumList)\n{\n    Console.WriteLine($"{item.m_Color}, {item.m_Number}");\n}