using System;\nusing System.Windows;\n\nnamespace PrintApp\n{\n    public partial class MainWindow : Window\n    {\n        public MainWindow()\n        {\n            InitializeComponent();\n            LoadFiles();\n        }\n\n        private void LoadFiles()\n        {\n            // Load file paths into ComboBox (for example purpose only)\n            fileComboBox.Items.Add("path_to_pdf.pdf");\n            fileComboBox.Items.Add("path_to_word.docx");\n            fileComboBox.Items.Add("path_to_excel.xlsx");\n            fileComboBox.Items.Add("path_to_powerpoint.pptx");\n        }\n\n        private void PrintButton_Click(object sender, RoutedEventArgs e)\n        {\n            string filePath = fileComboBox.SelectedItem.ToString();\n            string extension = System.IO.Path.GetExtension(filePath).ToLower();\n\n            switch (extension)\n            {\n                case ".pdf":\n                    PrintPdf(filePath);\n                    break;\n                case ".docx":\n                    PrintWordDocument(filePath);\n                    break;\n                case ".xlsx":\n                    PrintExcelDocument(filePath);\n                    break;\n                case ".pptx":\n                    PrintPowerPointPresentation(filePath);\n                    break;\n                default:\n                    MessageBox.Show("Unsupported file type");\n                    break;\n            }\n        }\n\n        private void PrintPdf(string filePath) { /* Implementation */ }\n        private void PrintWordDocument(string filePath) { /* Implementation */ }\n        private void PrintExcelDocument(string filePath) { /* Implementation */ }\n        private void PrintPowerPointPresentation(string filePath) { /* Implementation */ }\n    }\n}