using System;\nusing System.Globalization;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string[] timeFormats = { "h tt", "h:mm tt" };\n        string input1 = "8 a";\n        string input2 = "8:00 a";\n        \n        DateTime time;\n\n        if (DateTime.TryParseExact(input1, timeFormats, CultureInfo.InvariantCulture, DateTimeStyles.None, out time))\n        {\n            Console.WriteLine("Parsed Time: " + time.ToString("HH:mm"));\n        }\n        else\n        {\n            Console.WriteLine("Failed to parse time: " + input1);\n        }\n\n        if (DateTime.TryParseExact(input2, timeFormats, CultureInfo.InvariantCulture, DateTimeStyles.None, out time))\n        {\n            Console.WriteLine("Parsed Time: " + time.ToString("HH:mm"));\n        }\n        else\n        {\n            Console.WriteLine("Failed to parse time: " + input2);\n        }\n    }\n}