using System;\nusing System.Globalization;\nusing System.Collections.Generic;\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<string> list_numbers = new List<string> { "0.000", "123.45", "abc", "1.234e2" };\n        \n        foreach (string numberString in list_numbers)\n        {\n            double possibledouble;\n            bool isDouble = Double.TryParse(numberString, NumberStyles.Any, CultureInfo.InvariantCulture, out possibledouble);\n            \n            if (isDouble)\n            {\n                Console.WriteLine($"{numberString} is a valid double: {possibledouble}");\n            }\n            else\n            {\n                Console.WriteLine($"{numberString} is not a valid double.");\n            }\n        }\n    }\n}