<%@ Page Language="C#" AutoEventWireup="true" CodeFile="GetPrice.aspx.cs" Inherits="GetPrice" %>\n\n<script runat="server">\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Example data\n        string htmlContent = "<div>Price: $100</div>";\n        int additionalNumber = 42;\n\n        var response = new\n        {\n            html = htmlContent,\n            number = additionalNumber\n        };\n\n        string jsonResponse = new System.Web.Script.Serialization.JavaScriptSerializer().Serialize(response);\n\n        Response.ContentType = "application/json";\n        Response.Write(jsonResponse);\n        Response.End();\n    }\n</script>