using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic partial class MdiParentForm : Form\n{\n    public MdiParentForm()\n    {\n        InitializeComponent();\n        this.IsMdiContainer = true;\n    }\n\n    private void MdiParentForm_MdiChildActivate(object sender, EventArgs e)\n    {\n        if (this.ActiveMdiChild != null)\n        {\n            this.ActiveMdiChild.LocationChanged += ActiveMdiChild_LocationChanged;\n        }\n    }\n\n    private void ActiveMdiChild_LocationChanged(object sender, EventArgs e)\n    {\n        Form child = sender as Form;\n        if (child != null)\n        {\n            Rectangle parentBounds = this.ClientRectangle;\n            Rectangle childBounds = child.Bounds;\n\n            int newX = childBounds.X;\n            int newY = childBounds.Y;\n\n            if (childBounds.Left < parentBounds.Left)\n            {\n                newX = parentBounds.Left;\n            }\n            if (childBounds.Top < parentBounds.Top)\n            {\n                newY = parentBounds.Top;\n            }\n            if (childBounds.Right > parentBounds.Right)\n            {\n                newX = parentBounds.Right - childBounds.Width;\n            }\n            if (childBounds.Bottom > parentBounds.Bottom)\n            {\n                newY = parentBounds.Bottom - childBounds.Height;\n            }\n\n            child.Location = new Point(newX, newY);\n        }\n    }\n}