using System;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        int altitude = 9876;\n        string direction = "NE";\n        int windSpeed = 5;\n        string windDirection = "NE";\n\n        while (true)\n        {\n            RefreshDisplay(altitude, direction, windSpeed, windDirection);\n            Thread.Sleep(1000);  // Update every second\n\n            // For demo purposes, increment altitude and windSpeed\n            altitude++;\n            windSpeed++;\n        }\n    }\n\n    static void RefreshDisplay(int altitude, string direction, int windSpeed, string windDirection)\n    {\n        Console.Clear();\n        Console.SetCursorPosition(0, 0);\n        Console.WriteLine($"Altitude: {altitude}ft      Direction: {direction}");\n        Console.WriteLine($"Wind:     {windSpeed}km, {windDirection}");\n    }\n}