public class AuthService\n    {\n        public User Authenticate(string username, string password)\n        {\n            // Validate user credentials against the central database\n            var user = _centralDb.Users.SingleOrDefault(u => u.Username == username && u.Password == Hash(password));\n            if (user == null) throw new UnauthorizedAccessException("Invalid credentials");\n\n            return user;\n        }\n        \n        public string GetClientDatabase(User user)\n        {\n            // Retrieve the client database information for the authenticated user\n            return _centralDb.ClientInfo.SingleOrDefault(c => c.ClientId == user.ClientId)?.DatabaseName;\n        }\n    }