using System;\nusing OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\n\npublic class WebPageScreenshot\n{\n    public void CaptureScreenshot(string url, string saveLocation)\n    {\n        // Initialize ChromeDriver (ensure you have the ChromeDriver.exe in your PATH or specify the path to the executable)\n        using (IWebDriver driver = new ChromeDriver())\n        {\n            // Navigate to the URL\n            driver.Navigate().GoToUrl(url);\n\n            // Take a screenshot\n            Screenshot screenshot = ((ITakesScreenshot)driver).GetScreenshot();\n\n            // Save the screenshot\n            screenshot.SaveAsFile(saveLocation, ScreenshotImageFormat.Png);\n        }\n    }\n}\n\n// Usage\nclass Program\n{\n    static void Main()\n    {\n        WebPageScreenshot screenshot = new WebPageScreenshot();\n        screenshot.CaptureScreenshot("https://www.example.com", @"C:\path\to\save\screenshot.png");\n    }\n}