// Step 1: Fetch Orders\nstring sqlOrders = @"\n    SELECT * \n    FROM [Order] \n    WHERE OrderDate BETWEEN '2008-05-05' AND '2008-12-31'";\n\n// Execute and get list of Order IDs\n\n// Step 2: Fetch Order Details\nstring sqlOrderDetails = @"\n    SELECT * \n    FROM OrderDetail \n    WHERE OrderID IN (@OrderIDs)";\n\n// Step 3: Fetch Customers\nstring sqlCustomers = @"\n    SELECT * \n    FROM Customer \n    WHERE CustomerID IN (@CustomerIDs)";\n\n// Step 4: Fetch Customer Categories\nstring sqlCustomerCategories = @"\n    SELECT * \n    FROM CustomerCategory \n    WHERE CustomerCategoryID IN (@CustomerCategoryIDs)";