using System;\nusing Microsoft.Web.Administration;\n\nclass Program\n{\n    static void Main()\n    {\n        string siteName = "Default Web Site"; // Adjust according to your site\n        string virtualDirectoryName = "MedicatiebeheerFittests";\n        string physicalPath = @"C:\path\to\your\website";\n\n        using (ServerManager serverManager = new ServerManager())\n        {\n            Site site = serverManager.Sites[siteName];\n            if (site == null)\n            {\n                Console.WriteLine($"Site '{siteName}' not found.");\n                return;\n            }\n\n            Application app = site.Applications["/"];\n            if (app.VirtualDirectories[virtualDirectoryName] != null)\n            {\n                Console.WriteLine($"Virtual directory '{virtualDirectoryName}' already exists.");\n                return;\n            }\n\n            VirtualDirectory vDir = app.VirtualDirectories.CreateElement();\n            vDir.Path = "/" + virtualDirectoryName;\n            vDir.PhysicalPath = physicalPath;\n            app.VirtualDirectories.Add(vDir);\n\n            serverManager.CommitChanges();\n            Console.WriteLine("Virtual directory created successfully.");\n        }\n    }\n}