using System;\nusing System.Web.UI;\n\nnamespace YourNamespace\n{\n    public partial class Quiz : Page\n    {\n        protected void Page_Load(object sender, EventArgs e)\n        {\n            if (!IsPostBack)\n            {\n                // Generate dynamic HTML for the questionnaire\n                string html = "<label>Question 1: What is 2+2?</label><br/>";\n                html += "<input type='radio' name='Q1' value='3' /> 3<br/>";\n                html += "<input type='radio' name='Q1' value='4' /> 4<br/>";\n                html += "<input type='radio' name='Q1' value='5' /> 5<br/>";\n                \n                // Insert the HTML into the div container\n                quizContainer.InnerHtml = html;\n            }\n        }\n\n        protected void btnSubmit_Click(object sender, EventArgs e)\n        {\n            // Retrieve the answers using Request.Form\n            string answer1 = Request.Form["Q1"];\n\n            // Check the answers\n            if (answer1 == "4")\n            {\n                Response.Write("Question 1 is correct!");\n            }\n            else\n            {\n                Response.Write("Question 1 is incorrect.");\n            }\n        }\n    }\n}