using System;\nusing System.Collections;\nusing System.Collections.Generic;\n\nclass MyClass\n{\n}\n\nclass MainClass\n{\n    public static void Main(string[] args)\n    {\n        Type myType = typeof(MyClass);\n\n        // Create an instance of List<myType>\n        var listType = typeof(List<>).MakeGenericType(myType);\n        var myList = (IList)Activator.CreateInstance(listType);\n\n        // Example: Adding an instance of MyClass to the list\n        object myClassInstance = Activator.CreateInstance(myType);\n        myList.Add(myClassInstance);\n\n        Console.WriteLine("List created with type: " + myList.GetType());\n        Console.WriteLine("List contains: " + myList.Count + " items.");\n    }\n}