using System;\nusing Microsoft.Office.Interop.Outlook;\n\nnamespace OutlookFolderEnumerator\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            Application outlookApp = new Application();\n            NameSpace outlookNamespace = outlookApp.GetNamespace("MAPI");\n            Folders folders = outlookNamespace.Folders;\n\n            foreach (MAPIFolder folder in folders)\n            {\n                EnumerateFolders(folder);\n            }\n\n            Console.ReadKey();\n        }\n\n        private static void EnumerateFolders(MAPIFolder folder)\n        {\n            Console.WriteLine(folder.Name);\n\n            Folders subFolders = folder.Folders;\n            foreach (MAPIFolder subFolder in subFolders)\n            {\n                EnumerateFolders(subFolder);\n            }\n        }\n    }\n}