using HtmlAgilityPack;\nusing System;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        var html = @"<table name='important'>\n                        <tr>\n                          <td>Stuff I'm NOT interested in</td>\n                        </tr>\n                        <tr>\n                          <td>Stuff I'm interested in</td>\n                          <td><interestingtag/></td>\n                          <td>More stuff I'm interested in</td>\n                        </tr>\n                        <tr>\n                          <td>Stuff I'm NOT interested in</td>\n                        </tr>\n                        <tr>\n                          <td>Stuff I'm NOT interested in</td>\n                        </tr>\n                     </table>";\n\n        HtmlDocument doc = new HtmlDocument();\n        doc.LoadHtml(html);\n\n        var rows = doc.DocumentNode.SelectNodes("//table[@name='important']/tr[descendant::interestingtag]");\n\n        foreach (var row in rows)\n        {\n            Console.WriteLine(row.InnerHtml);\n        }\n    }\n}