using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string input = "Thís stríng cöntäins nön-ASCII characters: ñ, é, ü.";\n        string output = RemoveNonAsciiCharacters(input);\n        Console.WriteLine(output);\n    }\n\n    static string RemoveNonAsciiCharacters(string input)\n    {\n        // Replace non-ASCII characters with an empty string\n        return Regex.Replace(input, @"[^\x00-\x7F]", string.Empty);\n    }\n}