using System;\nusing System.Net.Sockets;\nusing System.Text;\n\nclass TcpClientExample\n{\n    static void Main()\n    {\n        string server = "cpapp";\n        int port = 500;\n        TcpClient client = new TcpClient(server, port);\n        NetworkStream stream = client.GetStream();\n\n        byte[] data = Encoding.UTF8.GetBytes("Your large message here");\n        int bufferSize = 8192; // 8 KB buffer size\n        int offset = 0;\n\n        while (offset < data.Length)\n        {\n            int chunkSize = Math.Min(bufferSize, data.Length - offset);\n            stream.Write(data, offset, chunkSize);\n            offset += chunkSize;\n        }\n\n        stream.Close();\n        client.Close();\n    }\n}