using System;\nusing System.Data;\nusing System.Xml;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Create a sample DataTable\n        DataTable table = new DataTable();\n        table.Columns.Add("ID", typeof(int));\n        table.Columns.Add("Name", typeof(string));\n        table.Columns.Add("Text", typeof(string));\n\n        table.Rows.Add(1, DBNull.Value, "test1");\n        table.Rows.Add(2, "toto", "test2");\n        table.Rows.Add(3, "tata", "test3");\n\n        // Replace DBNull values with an empty string\n        foreach (DataRow row in table.Rows)\n        {\n            foreach (DataColumn column in table.Columns)\n            {\n                if (row[column] == DBNull.Value)\n                {\n                    row[column] = string.Empty;\n                }\n            }\n        }\n\n        // Convert the DataTable to a DataSet\n        DataSet dataSet = new DataSet();\n        dataSet.Tables.Add(table);\n\n        // Serialize the DataSet to XML\n        XmlDocument xmlDocument = new XmlDocument();\n        using (var xmlWriter = xmlDocument.CreateNavigator().AppendChild())\n        {\n            dataSet.WriteXml(xmlWriter);\n        }\n\n        // Output the XML\n        Console.WriteLine(xmlDocument.OuterXml);\n    }\n}