using System;\n\npublic class TypeConverter\n{\n    public static object ConvertFromString(string valueToConvertFrom, Type convertToType)\n    {\n        try\n        {\n            // Convert.ChangeType works for types that implement IConvertible\n            return Convert.ChangeType(valueToConvertFrom, convertToType);\n        }\n        catch (InvalidCastException)\n        {\n            // Handle the case where the type does not implement IConvertible\n            throw new InvalidOperationException("The target type must implement IConvertible.");\n        }\n        catch (FormatException)\n        {\n            // Handle format exceptions if the string cannot be converted\n            throw new InvalidOperationException($"The value '{valueToConvertFrom}' is not in a format recognized by the target type.");\n        }\n        catch (OverflowException)\n        {\n            // Handle overflow exceptions if the value is out of range for the target type\n            throw new InvalidOperationException($"The value '{valueToConvertFrom}' is outside the range of the target type.");\n        }\n    }\n\n    public static void Main()\n    {\n        string valueToConvertFrom = "123";\n        Type convertToType = typeof(int);\n        \n        object result = ConvertFromString(valueToConvertFrom, convertToType);\n        Console.WriteLine(result);  // Output: 123\n\n        convertToType = typeof(DateTime);\n        valueToConvertFrom = "2023-10-10";\n        \n        result = ConvertFromString(valueToConvertFrom, convertToType);\n        Console.WriteLine(result);  // Output: 10/10/2023 00:00:00\n    }\n}