using System.Data.Linq;\nusing System.Data.Linq.Mapping;\n\n[Table(Name = "CandyBar")]\npublic class CandyBar\n{\n    [Column(IsPrimaryKey=true, IsDbGenerated=true)]\n    public int Id { get; set; }\n\n    [Column]\n    public string Name { get; set; }\n\n    // Association for the related DeliciousCandyBar\n    private EntityRef<DeliciousCandyBar> _DeliciousCandyBar;\n    [Association(Name="FK_CandyBar_DeliciousCandyBar", Storage="_DeliciousCandyBar", ThisKey="Id", OtherKey="CandyBarId", IsForeignKey=false)]\n    public DeliciousCandyBar DeliciousCandyBar\n    {\n        get { return this._DeliciousCandyBar.Entity; }\n        set { this._DeliciousCandyBar.Entity = value; }\n    }\n}\n\n[Table(Name = "DeliciousCandyBar")]\npublic class DeliciousCandyBar\n{\n    [Column]\n    public int DeliciousnessFactor { get; set; }\n\n    [Column(IsPrimaryKey=true)]\n    public int CandyBarId { get; set; } // FK to CandyBar.Id\n\n    // Association for the related CandyBar\n    private EntityRef<CandyBar> _CandyBar;\n    [Association(Name="FK_CandyBar_DeliciousCandyBar", Storage="_CandyBar", ThisKey="CandyBarId", OtherKey="Id", IsForeignKey=true)]\n    public CandyBar CandyBar\n    {\n        get { return this._CandyBar.Entity; }\n        set { this._CandyBar.Entity = value; }\n    }\n}