protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        BindGrid();\n    }\n    else\n    {\n        GridView1.DataSource = (DataTable)ViewState["GridViewData"];\n        GridView1.DataBind();\n    }\n}\n\nprivate void BindGrid()\n{\n    // Assuming GetGridData() fetches the data from your LINQ query\n    var data = GetGridData();\n    \n    GridView1.DataSource = data;\n    GridView1.DataBind();\n    \n    // Save the data in ViewState for postback\n    ViewState["GridViewData"] = data;\n}\n\nprotected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        // Your logic to hide links\n        var rowData = (YourDataType)e.Row.DataItem;\n        var link = (LinkButton)e.Row.FindControl("YourLinkButtonID");\n        \n        if (rowData.HasNoParent)\n        {\n            link.Visible = false;\n        }\n    }\n}