using System;\nusing System.Transactions;\n\npublic class BusinessLogicLayer\n{\n    private readonly DataAccessLayer _dataAccessLayer;\n\n    public BusinessLogicLayer(DataAccessLayer dataAccessLayer)\n    {\n        _dataAccessLayer = dataAccessLayer;\n    }\n\n    public void ExecuteBusinessOperation()\n    {\n        using (var scope = new TransactionScope())\n        {\n            try\n            {\n                _dataAccessLayer.Method1();\n                _dataAccessLayer.Method2();\n                _dataAccessLayer.Method3();\n                \n                // Complete the transaction. If an exception is thrown before this line, the transaction is aborted.\n                scope.Complete();\n            }\n            catch (Exception ex)\n            {\n                // Handle the exception as needed\n                // The transaction will be aborted automatically due to the using statement\n                throw;\n            }\n        }\n    }\n}\n\npublic class DataAccessLayer\n{\n    public void Method1()\n    {\n        // Perform database operations\n    }\n\n    public void Method2()\n    {\n        // Perform database operations\n    }\n\n    public void Method3()\n    {\n        // Perform database operations\n    }\n}