using System;\nusing System.Text;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string utf8String = "O’Brian and some special characters: äöüß";\n\n        // Create encoding objects\n        Encoding utf8 = new UTF8Encoding();\n        Encoding ascii = new ASCIIEncoding();\n\n        // Convert UTF-8 string to bytes\n        byte[] utf8Bytes = utf8.GetBytes(utf8String);\n\n        // Convert UTF-8 bytes to ASCII bytes\n        byte[] asciiBytes = Encoding.Convert(utf8, ascii, utf8Bytes);\n\n        // Convert ASCII bytes back to a string\n        string asciiString = ascii.GetString(asciiBytes);\n\n        Console.WriteLine("Original UTF-8 String: " + utf8String);\n        Console.WriteLine("Converted ASCII String: " + asciiString);\n    }\n}