using System;\nusing System.Text;\nusing Unidecode.NET;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string utf8String = "O’Brian and some special characters: äöüß";\n\n        // Transliterate the string using Unidecode.NET\n        string asciiString = utf8String.Unidecode();\n\n        Console.WriteLine("Original UTF-8 String: " + utf8String);\n        Console.WriteLine("Transliterated ASCII String: " + asciiString);\n    }\n}