using System;\nusing System.Data;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new DataSet\n        DataSet dataSet = new DataSet("MyDataSet");\n\n        // Define Parent Table\n        DataTable parentTable = new DataTable("Parent");\n        parentTable.Columns.Add("ID", typeof(int));\n        parentTable.Columns.Add("Name", typeof(string));\n        parentTable.PrimaryKey = new DataColumn[] { parentTable.Columns["ID"] };\n\n        // Define Child Table\n        DataTable childTable = new DataTable("Child");\n        childTable.Columns.Add("ID", typeof(int));\n        childTable.Columns.Add("ParentID", typeof(int));\n        childTable.Columns.Add("Value", typeof(string));\n\n        // Add tables to DataSet\n        dataSet.Tables.Add(parentTable);\n        dataSet.Tables.Add(childTable);\n\n        // Define the relation and add it to the DataSet\n        DataRelation relation = new DataRelation("ParentChild",\n            parentTable.Columns["ID"],\n            childTable.Columns["ParentID"]);\n        dataSet.Relations.Add(relation);\n\n        // Add some data to the tables\n        DataRow parentRow = parentTable.NewRow();\n        parentRow["ID"] = 1;\n        parentRow["Name"] = "Parent1";\n        parentTable.Rows.Add(parentRow);\n\n        DataRow childRow = childTable.NewRow();\n        childRow["ID"] = 1;\n        childRow["ParentID"] = 1;\n        childRow["Value"] = "Child1";\n        childTable.Rows.Add(childRow);\n\n        // Write the DataSet to XML\n        dataSet.WriteXml("output.xml", XmlWriteMode.WriteSchema);\n    }\n}