using System.Security.Cryptography;\nusing System.Text;\n\npublic static class SecureKeyStorage\n{\n    public static byte[] Protect(byte[] data)\n    {\n        // Protect data using the current user's credentials\n        return ProtectedData.Protect(data, null, DataProtectionScope.CurrentUser);\n    }\n\n    public static byte[] Unprotect(byte[] data)\n    {\n        // Unprotect data using the current user's credentials\n        return ProtectedData.Unprotect(data, null, DataProtectionScope.CurrentUser);\n    }\n\n    public static void Main()\n    {\n        string key = "YourSecretKeyHere";\n        byte[] keyBytes = Encoding.UTF8.GetBytes(key);\n\n        // Encrypt the key\n        byte[] protectedKey = Protect(keyBytes);\n        // Save protectedKey to a secure location\n\n        // Decrypt the key when needed\n        byte[] unprotectedKey = Unprotect(protectedKey);\n        string decryptedKey = Encoding.UTF8.GetString(unprotectedKey);\n    }\n}