protected void Page_Load(object sender, EventArgs e)\n{\n    if (IsPostBack)\n    {\n        float result;\n        if (!float.TryParse(txtFloatNumber.Text, NumberStyles.Float, CultureInfo.CurrentCulture, out result))\n        {\n            // Handle invalid float number input\n            revFloatValidator.ErrorMessage = "Please enter a valid float number according to your locale.";\n            revFloatValidator.IsValid = false;\n        }\n    }\n}