public bool ValidateHostname(string hostname)\n{\n    string hostnamePattern = @"^(?!-)[A-Za-z0-9-]{1,63}(?<!-)$";\n    Regex hostnameRegex = new Regex(hostnamePattern);\n    return hostnameRegex.IsMatch(hostname);\n}\n\npublic bool ValidateIPv4(string ipv4Address)\n{\n    string ipv4Pattern = @"^([0-9]{1,3}\.){3}[0-9]{1,3}$";\n    Regex ipv4Regex = new Regex(ipv4Pattern);\n    return ipv4Regex.IsMatch(ipv4Address);\n}\n\npublic bool ValidateDomain(string domain)\n{\n    string domainPattern = @"^(?!-)[A-Za-z0-9-]{1,63}(?<!-)(\.[A-Za-z0-9-]{1,63})*\.[A-Za-z]{2,6}$";\n    Regex domainRegex = new Regex(domainPattern);\n    return domainRegex.IsMatch(domain);\n}\n\npublic bool ValidateTXT(string txtRecord)\n{\n    string txtPattern = @"^[^;]{1,255}$";\n    Regex txtRegex = new Regex(txtPattern);\n    return txtRegex.IsMatch(txtRecord);\n}