// Service layer to orchestrate complex operations\npublic class BusinessObjectService\n{\n    private readonly BusinessObjectRepository _repository;\n\n    public BusinessObjectService(BusinessObjectRepository repository)\n    {\n        _repository = repository;\n    }\n\n    public void ProcessBusinessObject(int id)\n    {\n        BusinessObject bo = _repository.Load(id);\n        bo.Populate();\n        bo.Calculate();\n        _repository.Save(bo);\n    }\n}