try\n   {\n       var accessApp = new Microsoft.Office.Interop.Access.Application();\n       accessApp.OpenCurrentDatabase(@"C:\path\to\your\database.accdb");\n\n       // Perform the TransferDatabase operation\n       accessApp.DoCmd.TransferDatabase(\n           Microsoft.Office.Interop.Access.AcDataTransferType.acImport,\n           "ODBC Database",\n           "ODBC;DSN=YourDSN;UID=YourUsername;PWD=YourPassword",\n           Microsoft.Office.Interop.Access.AcObjectType.acTable,\n           "SourceTableName",\n           "DestinationTableName"\n       );\n\n       accessApp.CloseCurrentDatabase();\n       accessApp.Quit();\n   }\n   catch (Exception ex)\n   {\n       Console.WriteLine("An error occurred: " + ex.Message);\n       // Additional error handling logic\n   }