// Example using Azure Key Vault\nusing Azure.Identity;\nusing Azure.Security.KeyVault.Secrets;\n\npublic static class KeyVaultExample\n{\n    public static async Task<string> GetSecretFromKeyVault(string secretName)\n    {\n        var client = new SecretClient(new Uri("https://your-key-vault-name.vault.azure.net/"), new DefaultAzureCredential());\n        KeyVaultSecret secret = await client.GetSecretAsync(secretName);\n        return secret.Value;\n    }\n\n    public static async Task Main()\n    {\n        string key = await GetSecretFromKeyVault("YourSecretKeyName");\n        // Use key for your encryption/decryption operations\n    }\n}