using System.Diagnostics;\n\npublic class PerformanceCounterHelper\n{\n    private PerformanceCounter callsPerHourCounter;\n    private PerformanceCounter totalCallsCounter;\n\n    public PerformanceCounterHelper()\n    {\n        // Ensure the performance counters are created\n        if (!PerformanceCounterCategory.Exists("MyWebServiceCategory"))\n        {\n            CounterCreationDataCollection counters = new CounterCreationDataCollection();\n\n            CounterCreationData callsPerHour = new CounterCreationData();\n            callsPerHour.CounterName = "Calls Processed Per Hour";\n            callsPerHour.CounterType = PerformanceCounterType.RateOfCountsPerSecond32;\n            counters.Add(callsPerHour);\n\n            CounterCreationData totalCalls = new CounterCreationData();\n            totalCalls.CounterName = "Total Calls";\n            totalCalls.CounterType = PerformanceCounterType.NumberOfItems32;\n            counters.Add(totalCalls);\n\n            PerformanceCounterCategory.Create("MyWebServiceCategory", "Performance counters for my web service", PerformanceCounterCategoryType.SingleInstance, counters);\n        }\n\n        // Initialize the performance counters\n        callsPerHourCounter = new PerformanceCounter("MyWebServiceCategory", "Calls Processed Per Hour", false);\n        totalCallsCounter = new PerformanceCounter("MyWebServiceCategory", "Total Calls", false);\n    }\n\n    public void IncrementCounters()\n    {\n        callsPerHourCounter.Increment();\n        totalCallsCounter.Increment();\n    }\n}