protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Initial population of the GridView if needed\n        BindGrid();\n    }\n}\n\nprivate void BindGrid()\n{\n    // Replace this with your actual data retrieval logic\n    DataTable dt = GetData();\n    ResultGridView.DataSource = dt;\n    ResultGridView.DataBind();\n}\n\nprotected void ResultGridView_RowCommand(object sender, GridViewCommandEventArgs e)\n{\n    if (e.CommandName == "Select")\n    {\n        int id = Convert.ToInt32(e.CommandArgument);\n        // Redirect to the edit page with the selected ID\n        Response.Redirect("EditPage.aspx?id=" + id);\n    }\n}\n\nprivate DataTable GetData()\n{\n    // Mock data for demonstration purpose\n    DataTable dt = new DataTable();\n    dt.Columns.Add("ID", typeof(int));\n    dt.Columns.Add("Name", typeof(string));\n\n    // Populate with sample data\n    for (int i = 1; i <= 10; i++)\n    {\n        dt.Rows.Add(i, "Name " + i);\n    }\n\n    return dt;\n}