public class AnnouncementsController : Controller\n{\n    public JsonResult List(int count)\n    {\n        // Sample data\n        var announcements = new List<Announcement>\n        {\n            new Announcement { Title = "Announcement 1", PostedDate = DateTime.Now, Teaser = "Teaser 1", Link = "http://www.someotherdomain.com/1" },\n            new Announcement { Title = "Announcement 2", PostedDate = DateTime.Now, Teaser = "Teaser 2", Link = "http://www.someotherdomain.com/2" }\n            // add more announcements\n        };\n\n        return Json(announcements.Take(count), JsonRequestBehavior.AllowGet);\n    }\n}\n\npublic class Announcement\n{\n    public string Title { get; set; }\n    public DateTime PostedDate { get; set; }\n    public string Teaser { get; set; }\n    public string Link { get; set; }\n}