using System;\nusing System.IO;\nusing System.IO.Compression;\nusing System.Text;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string originalText = "This is the original text to be compressed and encoded.";\n        byte[] compressedData;\n\n        using (MemoryStream output = new MemoryStream())\n        {\n            using (GZipStream gzip = new GZipStream(output, CompressionMode.Compress))\n            {\n                byte[] inputBytes = Encoding.UTF8.GetBytes(originalText);\n                gzip.Write(inputBytes, 0, inputBytes.Length);\n            }\n            compressedData = output.ToArray();\n        }\n\n        string base64EncodedGzipData = Convert.ToBase64String(compressedData);\n        Console.WriteLine("Base64 Encoded GZip Data: " + base64EncodedGzipData);\n    }\n}