using System;\nusing System.Collections.Generic;\n\npublic class Program\n{\n    public static void Main()\n    {\n        foreach (var word in GetWords(4))\n        {\n            Console.WriteLine(word);\n        }\n    }\n\n    public static IEnumerable<string> GetWords(int length)\n    {\n        char[] letters = new char[length];\n        foreach (var word in GenerateWords(letters, 0))\n        {\n            yield return word;\n        }\n    }\n\n    private static IEnumerable<string> GenerateWords(char[] letters, int position)\n    {\n        if (position == letters.Length)\n        {\n            yield return new string(letters);\n        }\n        else\n        {\n            for (char c = 'A'; c <= 'Z'; c++)\n            {\n                letters[position] = c;\n                foreach (var word in GenerateWords(letters, position + 1))\n                {\n                    yield return word;\n                }\n            }\n        }\n    }\n}