using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string base64Pattern = @"^(?:[A-Za-z0-9+/]{4})*(?:[A-Za-z0-9+/]{2}(?:==)?|[A-Za-z0-9+/]{3}=?)?$";\n        Regex regex = new Regex(base64Pattern);\n\n        string input = "VGhpcyBpcyBzaW1wbGUgQVNDSUkgQmFzZTY0IGZvciBTdGFja092ZXJmbG93IGV4YW1wbGUu";\n        bool isValidBase64 = regex.IsMatch(input);\n\n        Console.WriteLine($"Is Valid Base64: {isValidBase64}");\n    }\n}