using System;\n    using System.Security.Cryptography;\n    using System.Text;\n\n    public class ConfigEncryption\n    {\n        public static string Encrypt(string data)\n        {\n            byte[] dataBytes = Encoding.Unicode.GetBytes(data);\n            byte[] encryptedBytes = ProtectedData.Protect(dataBytes, null, DataProtectionScope.LocalMachine);\n            return Convert.ToBase64String(encryptedBytes);\n        }\n\n        public static string Decrypt(string encryptedData)\n        {\n            byte[] encryptedBytes = Convert.FromBase64String(encryptedData);\n            byte[] dataBytes = ProtectedData.Unprotect(encryptedBytes, null, DataProtectionScope.LocalMachine);\n            return Encoding.Unicode.GetString(dataBytes);\n        }\n    }