public class BaseClass\n{\n    public BaseClass() { /* Constructor logic */ }\n    public BaseClass(int x) { /* Constructor logic */ }\n    public BaseClass(int x, string y) { /* Constructor logic */ }\n}\n\npublic class DerivedClass : BaseClass\n{\n    // Constructor that matches the parameterless base constructor\n    public DerivedClass() : base() { }\n\n    // Constructor that matches the base constructor with one int parameter\n    public DerivedClass(int x) : base(x) { }\n\n    // Constructor that matches the base constructor with an int and string parameter\n    public DerivedClass(int x, string y) : base(x, y) { }\n}