using System;\nusing System.IO;\nusing System.Web;\n\npublic class FileUploadHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        string uploadPath = context.Server.MapPath("~/Uploads");\n        if (!Directory.Exists(uploadPath))\n        {\n            Directory.CreateDirectory(uploadPath);\n        }\n\n        HttpPostedFile file = context.Request.Files["file"];\n        if (file != null)\n        {\n            string filePath = Path.Combine(uploadPath, Path.GetFileName(file.FileName));\n            using (var fs = new FileStream(filePath, FileMode.Append, FileAccess.Write))\n            {\n                byte[] buffer = new byte[4096];\n                int bytesRead;\n                while ((bytesRead = file.InputStream.Read(buffer, 0, buffer.Length)) > 0)\n                {\n                    fs.Write(buffer, 0, bytesRead);\n                }\n            }\n            context.Response.StatusCode = 200;\n            context.Response.Write("File uploaded successfully.");\n        }\n        else\n        {\n            context.Response.StatusCode = 400;\n            context.Response.Write("No file received.");\n        }\n    }\n\n    public bool IsReusable => false;\n}