using System;\nusing System.Web;\n\npublic class UrlHelper\n{\n    public static string GetAbsoluteUrl(string relativeUrl)\n    {\n        // Get the current request\n        HttpRequest request = HttpContext.Current.Request;\n        \n        // Get the base URL\n        Uri baseUri = new Uri(request.Url.GetLeftPart(UriPartial.Authority));\n        \n        // Combine the base URL with the relative URL\n        Uri absoluteUri = new Uri(baseUri, VirtualPathUtility.ToAbsolute(relativeUrl));\n        \n        // Return the absolute URL as a string\n        return absoluteUri.ToString();\n    }\n}\n\n// Usage example:\nstring absoluteUrl = UrlHelper.GetAbsoluteUrl("~/index.aspx");