DbCommand getIDcmd = connection.CreateCommand();\ngetIDcmd.CommandText = "select LAST_ID from SEQUENCE_ID";\nobject result = getIDcmd.ExecuteScalar();\n\nif (result != null && result is decimal)\n{\n    decimal decimalResult = (decimal)result;\n    sequenceID = (Int64)decimalResult;\n}\nelse\n{\n    throw new InvalidCastException("The returned value is not of type decimal.");\n}