using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\n\npublic class FileUploader\n{\n    public void UploadFile(string url, string filePath)\n    {\n        try\n        {\n            FileInfo fileInfo = new FileInfo(filePath);\n            string boundary = "---------------------------" + DateTime.Now.Ticks.ToString("x");\n            byte[] boundaryBytes = Encoding.ASCII.GetBytes("\r\n--" + boundary + "\r\n");\n\n            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n            request.ContentType = "multipart/form-data; boundary=" + boundary;\n            request.Method = "POST";\n            request.KeepAlive = true;\n            request.Credentials = CredentialCache.DefaultCredentials;\n            request.Timeout = System.Threading.Timeout.Infinite;\n            request.ReadWriteTimeout = System.Threading.Timeout.Infinite;\n            request.AllowWriteStreamBuffering = false;\n\n            using (Stream requestStream = request.GetRequestStream())\n            {\n                requestStream.Write(boundaryBytes, 0, boundaryBytes.Length);\n\n                // Write file content\n                string header = $"Content-Disposition: form-data; name=\"upfile\"; filename=\"{fileInfo.Name}\"\r\nContent-Type: application/octet-stream\r\n\r\n";\n                byte[] headerBytes = Encoding.UTF8.GetBytes(header);\n                requestStream.Write(headerBytes, 0, headerBytes.Length);\n\n                using (FileStream fileStream = new FileStream(filePath, FileMode.Open, FileAccess.Read))\n                {\n                    byte[] buffer = new byte[4096];\n                    int bytesRead;\n                    while ((bytesRead = fileStream.Read(buffer, 0, buffer.Length)) != 0)\n                    {\n                        requestStream.Write(buffer, 0, bytesRead);\n                    }\n                }\n\n                byte[] trailer = Encoding.ASCII.GetBytes("\r\n--" + boundary + "--\r\n");\n                requestStream.Write(trailer, 0, trailer.Length);\n            }\n\n            using (WebResponse response = request.GetResponse())\n            {\n                using (Stream responseStream = response.GetResponseStream())\n                {\n                    using (StreamReader reader = new StreamReader(responseStream))\n                    {\n                        string responseText = reader.ReadToEnd();\n                        Console.WriteLine(responseText);\n                    }\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}