using RazorEngine;\n   using RazorEngine.Templating; // For the template service.\n   using System.Collections.Generic;\n\n   public class Item\n   {\n       public string StartDate { get; set; }\n       public int Id { get; set; }\n       public int WorkItemId { get; set; }\n       public string AccountNumber { get; set; }\n       public string FirstName { get; set; }\n       public string LastName { get; set; }\n       public string Address { get; set; }\n       public string Description { get; set; }\n   }\n\n   public class ReportGenerator\n   {\n       public string GenerateHtmlReport(List<Item> items)\n       {\n           string template = System.IO.File.ReadAllText("ReportTemplate.cshtml");\n\n           var result = Engine.Razor.RunCompile(template, "reportTemplateKey", typeof(List<Item>), items);\n\n           return result;\n       }\n   }