using System;\n\npublic class BaseTypeTree\n{\n    public static void PrintBaseTypes(Type type)\n    {\n        Console.WriteLine($"Base types of {type.FullName}:");\n        Type currentType = type;\n        while (currentType != null)\n        {\n            Console.WriteLine(currentType.FullName);\n            currentType = currentType.BaseType;\n        }\n    }\n}\n\n// Usage example:\nBaseTypeTree.PrintBaseTypes(typeof(System.Windows.Forms.MenuItem));