using System;\nusing System.Data.SqlServerCe;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Data Source=MyDatabase.sdf;Persist Security Info=False;";\n\n        using (SqlCeConnection connection = new SqlCeConnection(connectionString))\n        {\n            connection.Open();\n\n            string query = "SELECT * FROM MyTable";\n            using (SqlCeCommand command = new SqlCeCommand(query, connection))\n            {\n                SqlCeResultSet resultSet = command.ExecuteResultSet(ResultSetOptions.Updatable);\n\n                SqlCeUpdatableRecord record = resultSet.CreateRecord();\n\n                // Assuming you have data in memory stored in a list of some custom objects\n                var dataList = GetDataFromMemory();\n\n                foreach (var data in dataList)\n                {\n                    record.SetValue(0, data.Column1);\n                    record.SetValue(1, data.Column2);\n                    // Set other column values as needed\n\n                    resultSet.Insert(record);\n                }\n            }\n        }\n    }\n\n    static List<CustomData> GetDataFromMemory()\n    {\n        // Implement this method to retrieve your data from memory\n        return new List<CustomData>\n        {\n            new CustomData { Column1 = "Value1", Column2 = "Value2" },\n            // Add more data as needed\n        };\n    }\n}\n\npublic class CustomData\n{\n    public string Column1 { get; set; }\n    public string Column2 { get; set; }\n    // Add other properties as needed\n}