using System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\npublic class PDFMerger\n{\n    public static void MergePDFs(string sourceFile1, string sourceFile2, string outputFile)\n    {\n        using (FileStream stream = new FileStream(outputFile, FileMode.Create))\n        {\n            Document document = new Document();\n            PdfCopy copy = new PdfCopy(document, stream);\n            document.Open();\n\n            // Add the first PDF\n            PdfReader reader1 = new PdfReader(sourceFile1);\n            for (int i = 1; i <= reader1.NumberOfPages; i++)\n            {\n                copy.AddPage(copy.GetImportedPage(reader1, i));\n            }\n            reader1.Close();\n\n            // Add the second PDF\n            PdfReader reader2 = new PdfReader(sourceFile2);\n            for (int i = 1; i <= reader2.NumberOfPages; i++)\n            {\n                copy.AddPage(copy.GetImportedPage(reader2, i));\n            }\n            reader2.Close();\n\n            document.Close();\n        }\n    }\n}