using System;\nusing System.Xml;\nusing System.Xml.XPath;\n\nclass Program\n{\n    static void Main()\n    {\n        string xml = @"<?xml version='1.0'?>\n        <!-- a fragment of a book store inventory database -->\n        <bookstore xmlns:bk='urn:samples'>\n          <book genre='novel' publicationdate='1997' bk:ISBN='1-861001-57-8'>\n            <title>Pride And Prejudice</title>\n            <author>\n              <first-name>Jane</first-name>\n              <last-name>Austen</last-name>\n            </author>\n            <price>24.95</price>\n          </book>\n          <book genre='novel' publicationdate='1992' bk:ISBN='1-861002-30-1'>\n            <title>The Handmaid's Tale</title>\n            <author>\n              <first-name>Margaret</first-name>\n              <last-name>Atwood</last-name>\n            </author>\n            <price>29.95</price>\n          </book>\n          <book genre='novel' publicationdate='1991' bk:ISBN='1-861001-57-6'>\n            <title>Emma</title>\n            <author>\n              <first-name>Jane</first-name>\n              <last-name>Austen</last-name>\n            </author>\n            <price>19.95</price>\n          </book>\n          <book genre='novel' publicationdate='1982' bk:ISBN='1-861001-45-3'>\n            <title>Sense and Sensibility</title>\n            <author>\n              <first-name>Jane</first-name>\n              <last-name>Austen</last-name>\n            </author>\n            <price>19.95</price>\n          </book>\n        </bookstore>";\n\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml(xml);\n\n        XPathNavigator navigator = doc.CreateNavigator();\n        XmlNamespaceManager nsManager = new XmlNamespaceManager(navigator.NameTable);\n        nsManager.AddNamespace("bk", "urn:samples");\n\n        // Select the ISBN of the book with title "Pride And Prejudice"\n        string isbnQuery = "/bookstore/book[title='Pride And Prejudice']/@bk:ISBN";\n        XPathExpression expr = navigator.Compile(isbnQuery);\n        expr.SetContext(nsManager);\n\n        string isbn = (string)navigator.Evaluate(expr);\n        Console.WriteLine("ISBN: " + isbn);\n    }\n}