static void Main(string[] args)\n{\n    var client = new MyServiceClient();\n    var sortableList = client.GetBusinessObjects();\n\n    // Now you can sort the list on the client side\n    sortableList.ApplySortCore(TypeDescriptor.GetProperties(typeof(MyBusinessObject))["Name"], ListSortDirection.Ascending);\n\n    foreach (var item in sortableList)\n    {\n        Console.WriteLine($"{item.Id} - {item.Name}");\n    }\n}